class TestSubscript:
    def test_subscript_basic(self):
        md = mistune.create_markdown(plugins=['subscript'])
        text = 'H~2~O is water'
        result = md(text)
        assert '<sub>2</sub>' in result

    def test_subscript_multiple(self):
        md = mistune.create_markdown(plugins=['subscript'])
        text = 'a~1~ and b~2~'
        result = md(text)
        assert result.count('<sub>') == 2


class TestFormattingEdgeCases:
    def test_strikethrough_with_spaces(self):
        md = mistune.create_markdown(plugins=['strikethrough'])
        text = '~~multiple words here~~'
        result = md(text)
        assert '<del>multiple words here</del>' in result

    def test_strikethrough_nested_emphasis(self):
        md = mistune.create_markdown(plugins=['strikethrough'])
        text = '~~**bold strike**~~'
        result = md(text)
        assert '<del>' in result
        assert '<strong>' in result

    def test_mark_with_escaped_equals(self):
        md = mistune.create_markdown(plugins=['mark'])
        text = '==mark \\=\\= equal=='
        result = md(text)
        assert '<mark>' in result

    def test_insert_single_character(self):
        md = mistune.create_markdown(plugins=['insert'])
        text = '^^x^^'
        result = md(text)
        assert '<ins>x</ins>' in result

    def test_superscript_with_escaped_space(self):
        md = mistune.create_markdown(plugins=['superscript'])
        text = 'x^a\\ b^'
        result = md(text)
        assert '<sup>a b</sup>' in result

    def test_subscript_with_escaped_tilde(self):
        md = mistune.create_markdown(plugins=['subscript'])
        text = 'formula~\\~test~'
        result = md(text)
        assert '<sub>' in result

    def test_multiple_formatting_plugins(self):
        md = mistune.create_markdown(plugins=['strikethrough', 'mark', 'insert', 'superscript', 'subscript'])
        text = '~~strike~~ ==mark== ^^insert^^ x^2^ H~2~O'
