import re
from abc import abstractmethod
from unittest import TestCase

from tests import fixtures


class BaseTestCase(TestCase):
    @classmethod
    def load_fixtures(cls, case_file: str) -> None:
        def attach_case(n: str, text: str, html: str) -> None:
            def method(self: "BaseTestCase") -> None:
                self.assert_case(n, text, html)

            name = "test_{}".format(n)
            method.__name__ = name
            method.__doc__ = "Run fixture {} - {}".format(case_file, n)
            setattr(cls, name, method)

        for n, text, html in fixtures.load_examples(case_file):
            if cls.ignore_case(n):
                continue
            attach_case(n, text, html)

    @classmethod
    def ignore_case(cls, name: str) -> bool:
        return False

    @abstractmethod
    def parse(self, text: str) -> str: ...

    def assert_case(self, name: str, text: str, html: str) -> None:
        result = self.parse(text)
        self.assertEqual(result, html)


def normalize_html(html: str) -> str:
    html = re.sub(r">\n+", ">", html)
    html = re.sub(r"\n+<", "<", html)
    return html.strip()

import os
import re
import json
from typing import Any, Iterable, Tuple

ROOT = os.path.join(os.path.dirname(__file__))

EXAMPLE_PATTERN = re.compile(
    r"^`{32} example\n([\s\S]*?)"
    r"^\.\n([\s\S]*?)"
    r"^`{32}$|^#{1,6} *(.*)$",
    flags=re.M,
)


def load_ast(filename: str) -> Any:
    with open(os.path.join(ROOT, "ast", filename)) as f:
        return json.load(f)


def load_json(filename: str) -> Any:
    with open(os.path.join(ROOT, filename)) as f:
        return json.load(f)


def load_examples(filename: str) -> Iterable[Tuple[str, str, str]]:
    if filename.endswith(".json"):
        data = load_json(filename)
        for item in data:
            section = item["section"].lower().replace(" ", "_")
            n = "%s_%03d" % (section, item["example"])
            yield n, item["markdown"], item["html"]
    else:
        with open(os.path.join(ROOT, filename), "rb") as f:
            content = f.read()
            s = content.decode("utf-8")
            yield from parse_examples(s)


def parse_examples(text: str) -> Iterable[Tuple[str, str, str]]:
    data = EXAMPLE_PATTERN.findall(text)

    section = None
    count = 0
    for md, html, title in data:
        if title:
            count = 0
            section = title.lower().replace(" ", "_")

        if md and html:
            count += 1
            n = "%s_%03d" % (section, count)
            md = md.replace("\u2192", "\t")
            html = html.replace("\u2192", "\t")
            yield n, md, html

